.card {
    color: #474747;
    background-color: #FFFFFF;
    box-shadow: 0 4px 8px 0 rgba(0,0,0,0.2);
    transition: 0.3s;
    border-radius: 10px; 
    margin: 16px 16px;
    text-decoration: none;
}

.card:hover {
    box-shadow: 0 8px 16px 0 rgba(0,0,0,0.2);
}

.container {
    padding: 2px 16px;
}

.container-buffer {
  padding-top: 28px;
  padding-bottom: 0;
  margin-bottom: 0;
}

.card-group {
  color: #4682B4;
  font-family: 'Pacifico', cursive;
  font-size: 18px;
  padding: 4px 4px;
}

.card-header {
  font-size: 24px;
  padding: 4px 4px;
}

.card-header:hover{
  transform:scale(1)
}

.card-time {
  padding: 4px 4px;
}